using UnityEngine; using System.Collections; public class Cube : MonoBehaviour {     bool button;     public Vector3 rValue = Vector3.zero;      // Use this for initialization     void Start()     {     }      public float speed = 10f;      // Update is called once per frame     void Update(){
             transform.Rotate(0, speed, 0);          if (Input.GetKey(KeyCode.W))         {             transform.position += new Vector3(0, 0, 1 * Time.deltaTime);         }         if (Input.GetKey(KeyCode.S))         {             transform.position += new Vector3(0, 0, -1 * Time.deltaTime);         }         if (Input.GetKey(KeyCode.D))         {             transform.position += new Vector3(1 * Time.deltaTime, 0, 0);         }         if (Input.GetKey(KeyCode.A))         {             transform.position += new Vector3(-1 * Time.deltaTime, 0, 0);         }
        //↓ここから回転コード
        if (Input.GetKey(KeyCode.T))         {             transform.Rotate(50 * Time.deltaTime, 20, 0);         }         if (Input.GetKey(KeyCode.G))         {             transform.Rotate(-50 * Time.deltaTime, 20, 0);         }         if (Input.GetKey(KeyCode.H))         {             transform.Rotate(0, 0, -50 * Time.deltaTime);         }         if (Input.GetKey(KeyCode.F))         {             transform.Rotate(0, 0, 50 * Time.deltaTime);         }     } }